import React, { useState } from "react";
import {
  Box,
  Form,
  Input,
  Select,
  Field,
  Button,
  Text,
  Checkbox,
  Radio,
  Flex,
  Card,
  Icon,
  MetaMaskButton
} from "rimble-ui";
import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import { SupplyChain } from "./abi/abi.js";

const web3 = new Web3(Web3.givenProvider);
const contractAddress = "0xA05De8c36234Fb74a0FD6f216a3568dbBe5400Eb";
const scContract = new web3.eth.Contract(SupplyChain, contractAddress);


function App() {
  // const [count, setUint] = useState(0);
  const [count, setGet] = useState("Count is...");
  const [product, setProduct] = useState("Item...");

  const addItem = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    // Convert to hooks
    const productName = "Tablet";
    const productPrice = 42;

    await scContract.methods.addItem(productName, productPrice).send({from: account});

    const gas = await scContract.methods.addItem(productName, productPrice).estimateGas();
    const post = await scContract.methods.addItem(productName, productPrice).send({
      from: account,
      gas,
    });
  };

  const getCount = async (t) => {
    t.preventDefault();
    const post = await scContract.methods.getCount().call();
    setGet(post);
  }

  const getProduct = async (t) => {
    t.preventDefault();
    const productNumber = 0;
    const post = await scContract.methods.fetchItem(productNumber).call();
    setProduct(post.name);
  }

  return (
    <div className="App">
      <h1 className="App-header">Project Marketplace
        <p></p>
        <MetaMaskButton.Outline size={'medium'}>Connect with MetaMask</MetaMaskButton.Outline>
      </h1>

      <Flex>
        <Box p={3} width={1 / 2} color="salmon" bg="black">
          Enter as Seller
        </Box>
        <Box p={3} width={1 / 2} color="white" bg="green">
          Enter as Buyer
        </Box>
      </Flex>

      <Card width={"auto"} maxWidth={"420px"} mx={"auto"} my={5} p={0}>
        <Image
          width={1}
          src="https://source.unsplash.com/random/1280x720"
          alt="random image from unsplash.com"
        />

        <Box px={[3, 3, 4]} py={3}>
          <Heading.h2>Card title</Heading.h2>
          <Heading.h5 color="#666">Card sub-title</Heading.h5>
        </Box>

        <Flex px={[3, 3, 4]} height={3} borderTop={1} borderColor={"#E8E8E8"}>
          <Button.Text p={"0"} mr={[2, 3, 4]} height={"auto"}>
            Text Button
    </Button.Text>

          <Button.Text p={"0"} height={"auto"}>
            Text Button
    </Button.Text>
        </Flex>
      </Card>

      <button onClick={addItem}> Add item </button>

      <button onClick={getCount}> Count items </button>

      <div>{count}</div>

      <button onClick={getProduct}> Get name </button>

      <div>{product}</div>

    </div>
    );
  // return (
    // <div className="App">
    //   <header className="App-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>
    //       Edit <code>src/App.js</code> and save to reload.
    //     </p>
    //     <a
    //       className="App-link"
    //       href="https://reactjs.org"
    //       target="_blank"
    //       rel="noopener noreferrer"
    //     >
    //       Learn React
    //     </a>
    //   </header>
    // </div>
  // );
}

export default App;
