import React, { useState } from "react";
import { Box, Form, Input, Select, Field, Button, Text, Checkbox, Radio, Flex, Card, Icon, MetaMaskButton, Image, Heading } from "rimble-ui";
// import { useWeb3Context } from 'web3-react'
import logo from './logo.svg';
import './App.css';
import Web3 from "web3";
import { SupplyChain } from "./abi/abi.js";

const web3 = new Web3(Web3.givenProvider);
const contractAddress = "0xA05De8c36234Fb74a0FD6f216a3568dbBe5400Eb";
const scContract = new web3.eth.Contract(SupplyChain, contractAddress);


function App() {
  // const context = useWeb3Context()
  const [count, setGet] = useState("Count is...");
  const [product, setProduct] = useState("Item...");
  const [inputName, setInputName] = useState("");


  const addItem = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];

    // Convert to hooks
    const productName = "Tablet";
    const productPrice = 42;

    await scContract.methods.addItem(productName, productPrice).send({from: account});

    const gas = await scContract.methods.addItem(productName, productPrice).estimateGas();
    const post = await scContract.methods.addItem(productName, productPrice).send({
      from: account,
      gas,
    });
  };

  const getCount = async (t) => {
    t.preventDefault();
    const post = await scContract.methods.getCount().call();
    setGet(post);
  }

  const getProduct = async (t) => {
    t.preventDefault();
    const productNumber = 0;
    const post = await scContract.methods.fetchItem(productNumber).call();
    setProduct(post.name);
  }

  const handleInput = e => {
    setInputName(e.target.value);
  };

  return (
    <div className="App">
      <h1 className="App-header">Project Marketplace
        <p></p>
        <MetaMaskButton.Outline size={'medium'}>Connect with MetaMask</MetaMaskButton.Outline>
      </h1>

      <h2>Smart Contract Tests</h2>

      <p>Contract: <strong> Marketplace </strong> </p>
      <p>Connected account: <strong> {/* {this.state.accounts} */} </strong> </p>
      <p>Products online: <strong> {count} </strong> </p>

      <Flex justifyContent='center'>
        <Box>
          <Form /* onSubmit={this.handleSubmitAddItem.bind(this)} */>
            <Input type="text" placeholder="Enter product name" /* value={inputName} onChange={handleInput} */ required={true} />
            <Input type="text" placeholder="Enter price e.g. $150" /* value={this.state.inputProductPrice} onChange={this.handleInputPrice.bind(this)} */ required={true} />
            <Input type="submit" value="Add product" />
          </Form>
        </Box>
      </Flex>

      <button onClick={addItem}> Add item </button>

      <button onClick={getCount}> Count items </button>

      <div>{count}</div>

      <button onClick={getProduct}> Get name </button>

      <div>{product}</div>

    </div>
    );
  // return (
    // <div className="App">
    //   <header className="App-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>
    //       Edit <code>src/App.js</code> and save to reload.
    //     </p>
    //     <a
    //       className="App-link"
    //       href="https://reactjs.org"
    //       target="_blank"
    //       rel="noopener noreferrer"
    //     >
    //       Learn React
    //     </a>
    //   </header>
    // </div>
  // );
}

export default App;
